import sys
from collections import deque

def bfs(graph, start, end):
    """Breadth-first search for shortest path."""
    queue = deque([(start, 0)])
    visited = set()

    while queue:
        node, depth = queue.popleft()
        if node == end:
            return depth
        if node in visited:
            continue
        visited.add(node)

        for neighbor in graph[node]:
            queue.append((neighbor, depth + 1))

    return -1

def main():
